				editor.mouse.touchedItemIndex = touchedItemIndex;
				editor.mouse.touchedApIndex = touchedApIndex;
				editor.mouse.touchedAPAHKind = touchedAPAHKind;
				Focus_addAP(touchedItemIndex, touchedApIndex);
