function newName(src){
	let head = src;
	let count = -1;
	const m = src.match(/^(.*)([0-9]+)$/);
	if(!! m){
		head = m[1];
		count = parseInt(m[2], 10);
	}
	let name = src;

	while(undefined !== getItemFromName(name)){
		count++;
		name = `${head}${count}`;
	}
	return name;
}
